/* fills the audio FIFOs with the audio samples when called and returns*/
void beep(void) {
    audio_ptr->control = 0b1100;
    audio_ptr->control = 0x0; // clears then resets the I/O FIFOs

    for (int i = 0; i < NUM_SAMPLES; i++) {
        while (!audio_ptr->wslc);
        audio_ptr->ldata = SAMPLES[i];
        audio_ptr->rdata = SAMPLES[i];
    }

    return;
}

